import React, { useState, useEffect, useMemo, useCallback, useRef } from 'react';
import {
  Github,
  Linkedin,
  Mail,
  Download,
  Award,
  Code,
  Terminal,
  Sun,
  Moon,
  ExternalLink,
  ChevronRight,
  Play,
  Sparkles,
  Zap,
  Rocket,
  ArrowUp,
  Phone,
  MapPin,
  Calendar,
  ChevronLeft,
  Copy,
  Check
} from 'lucide-react';

// Constants with Enhanced Performance
const ANIMATION_DELAY_INCREMENT = 0.15;
const GALLERY_AUTO_ROTATE_INTERVAL = 5000;
const SCROLL_THRESHOLD = 150;
const MOUSE_PARALLAX_INTENSITY = 15;
const THROTTLE_DELAY = 16; // ~60fps

// Modern Color Palette
const COLOR_THEME = {
  bg: {
    primary: '#0A0F1E',
    secondary: '#141B2D',
    card: '#1A2332',
    hover: '#202A3F'
  },
  accent: {
    cyan: '#06B6D4',
    purple: '#8B5CF6',
    pink: '#EC4899',
    emerald: '#10B981'
  },
  text: {
    primary: '#F1F5F9',
    secondary: '#94A3B8',
    muted: '#64748B'
  }
};

// Utility: Throttle function
const throttle = (func, delay) => {
  let timeoutId;
  let lastRan;
  return function(...args) {
    if (!lastRan) {
      func.apply(this, args);
      lastRan = Date.now();
    } else {
      clearTimeout(timeoutId);
      timeoutId = setTimeout(() => {
        if (Date.now() - lastRan >= delay) {
          func.apply(this, args);
          lastRan = Date.now();
        }
      }, delay - (Date.now() - lastRan));
    }
  };
};

// Utility: Magnetic Button Effect Hook
const useMagneticEffect = () => {
  const buttonRef = useRef(null);

  useEffect(() => {
    const button = buttonRef.current;
    if (!button) return;

    const handleMouseMove = (e) => {
      const rect = button.getBoundingClientRect();
      const x = e.clientX - rect.left - rect.width / 2;
      const y = e.clientY - rect.top - rect.height / 2;

      button.style.transform = `translate(${x * 0.3}px, ${y * 0.3}px)`;
    };

    const handleMouseLeave = () => {
      button.style.transform = 'translate(0, 0)';
    };

    button.addEventListener('mousemove', handleMouseMove);
    button.addEventListener('mouseleave', handleMouseLeave);

    return () => {
      button.removeEventListener('mousemove', handleMouseMove);
      button.removeEventListener('mouseleave', handleMouseLeave);
    };
  }, []);

  return buttonRef;
};

// Enhanced Interactive Terminal Component
const InteractiveTerminal = ({ onNavigate, handleResumeView, handleResumeDownload }) => {
  const [inputValue, setInputValue] = useState('');
  const [history, setHistory] = useState(['Type "help" to see available commands']);
  const [historyIndex, setHistoryIndex] = useState(-1);
  const [commandHistory, setCommandHistory] = useState([]);
  const terminalRef = useRef(null);
  const inputRef = useRef(null);

  const commands = {
    help: {
      description: 'Show all available commands',
      execute: () => [
        'ðŸ“‹ Available Commands:',
        'â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€',
        'help              - Show this help message',
        'whoami            - Display your information',
        'skills            - Navigate to skills section',
        'projects          - Navigate to projects section',
        'achievements      - Navigate to achievements section',
        'contact           - Navigate to contact section',
        'resume view       - Open resume in new tab',
        'resume dl         - Download resume',
        'clear             - Clear terminal screen',
        'â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€',
        'ðŸ’¡ Tip: Use arrow keys to navigate command history'
      ]
    },
    whoami: {
      description: 'Display your information',
      execute: () => [
        'ðŸ‘¤ Shanavas V Basheer',
        'â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€',
        'ðŸ“ Location: Kochi, Kerala, India',
        'ðŸŽ“ Education: M.Voc in Software Application Development (CUSAT)',
        'ðŸ’¼ Role: Full-Stack Developer | Competitive Coder',
        'ðŸ“§ Email: shanavasvbasheer@gmail.com',
        'ðŸ“± Phone: +91 85473 63158',
        'ðŸ’» GitHub: github.com/shanavasvbasheer',
        'ðŸ”— LinkedIn: linkedin.com/in/shanavasvbasheer',
        'â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€',
        'âœ¨ Status: Available for opportunities'
      ]
    },
    skills: {
      description: 'Navigate to skills section',
      execute: () => {
        onNavigate('skills');
        return ['âœ… Navigating to Skills section...'];
      }
    },
    projects: {
      description: 'Navigate to projects section',
      execute: () => {
        onNavigate('projects');
        return ['âœ… Navigating to Projects section...'];
      }
    },
    achievements: {
      description: 'Navigate to achievements section',
      execute: () => {
        onNavigate('achievements');
        return ['âœ… Navigating to Achievements section...'];
      }
    },
    contact: {
      description: 'Navigate to contact section',
      execute: () => {
        onNavigate('contact');
        return ['âœ… Navigating to Contact section...'];
      }
    },
    'resume view': {
      description: 'Open resume in new tab',
      execute: () => {
        handleResumeView();
        return ['âœ… Opening resume in new tab...'];
      }
    },
    'resume dl': {
      description: 'Download resume',
      execute: () => {
        handleResumeDownload();
        return ['âœ… Downloading resume...'];
      }
    },
    clear: {
      description: 'Clear terminal screen',
      execute: () => null
    }
  };

  const executeCommand = (cmd) => {
    const trimmedCmd = cmd.trim().toLowerCase();

    if (trimmedCmd === '') {
      return;
    }

    setCommandHistory([...commandHistory, trimmedCmd]);
    setHistoryIndex(-1);

    if (trimmedCmd === 'clear') {
      setHistory([]);
      setInputValue('');
      return;
    }

    const command = commands[trimmedCmd];

    let output;
    if (command) {
      output = command.execute();
    } else {
      output = [`âŒ Command not found: "${trimmedCmd}". Type "help" for available commands.`];
    }

    if (output) {
      setHistory([...history, `$ ${cmd}`, ...output, '']);
    }
    setInputValue('');
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Enter') {
      e.preventDefault();
      executeCommand(inputValue);
    } else if (e.key === 'ArrowUp') {
      e.preventDefault();
      if (historyIndex < commandHistory.length - 1) {
        const newIndex = historyIndex + 1;
        setHistoryIndex(newIndex);
        setInputValue(commandHistory[commandHistory.length - 1 - newIndex]);
      }
    } else if (e.key === 'ArrowDown') {
      e.preventDefault();
      if (historyIndex > 0) {
        const newIndex = historyIndex - 1;
        setHistoryIndex(newIndex);
        setInputValue(commandHistory[commandHistory.length - 1 - newIndex]);
      } else if (historyIndex === 0) {
        setHistoryIndex(-1);
        setInputValue('');
      }
    }
  };

  useEffect(() => {
    if (terminalRef.current) {
      terminalRef.current.scrollTop = terminalRef.current.scrollHeight;
    }
  }, [history]);

  return (
    <div className="w-full rounded-xl overflow-hidden shadow-2xl border-2 border-cyan-500/30 hover:border-cyan-500/60 transition-all duration-300 hover:shadow-cyan-500/20 hover:shadow-2xl">
      {/* Traffic Light Buttons */}
      <div className="bg-gray-800 px-4 py-3 flex gap-3 items-center border-b border-gray-700">
        <div className="flex gap-2">
          <div className="w-3 h-3 rounded-full bg-red-500 hover:bg-red-600 cursor-pointer transition-colors" title="Close" />
          <div className="w-3 h-3 rounded-full bg-yellow-500 hover:bg-yellow-600 cursor-pointer transition-colors" title="Minimize" />
          <div className="w-3 h-3 rounded-full bg-green-500 hover:bg-green-600 cursor-pointer transition-colors" title="Maximize" />
        </div>
        <div className="ml-4 text-gray-400 text-sm font-mono flex-1">Portfolio Terminal</div>
        <Terminal size={16} className="text-cyan-400" />
      </div>

      {/* Terminal Content */}
      <div
        ref={terminalRef}
        className="bg-gray-900 p-6 font-mono text-sm h-96 overflow-y-auto scrollbar-hide text-gray-100"
      >
        {history.map((line, i) => (
          <div
            key={i}
            className={`${
              line.startsWith('$') ? 'text-cyan-400 font-semibold' :
              line.startsWith('âœ…') ? 'text-emerald-400' :
              line.startsWith('âŒ') ? 'text-red-400' :
              line.startsWith('ðŸ“‹') || line.startsWith('ðŸ‘¤') || line.startsWith('â”€') ? 'text-cyan-300' :
              'text-gray-100'
            }`}
            style={{ whiteSpace: 'pre-wrap', wordBreak: 'break-word' }}
          >
            {line}
          </div>
        ))}
      </div>

      {/* Terminal Input */}
      <div className="bg-gray-800 px-6 py-4 border-t border-gray-700 flex items-center gap-2">
        <span className="text-cyan-400 font-mono font-semibold">svb@portfolio:~$</span>
        <input
          ref={inputRef}
          type="text"
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
          onKeyDown={handleKeyDown}
          placeholder="Type a command..."
          className="flex-1 bg-transparent outline-none text-white font-mono placeholder-gray-600 focus:placeholder-gray-500 transition-colors"
          autoFocus
        />
      </div>
    </div>
  );
};

// Enhanced Skill Card Component with Improved Visuals
const SkillCard = ({ skill, imageErrors, handleImageError, index }) => {
  const [isHovered, setIsHovered] = useState(false);

  return (
    <div
      className="group relative overflow-hidden rounded-xl p-8 bg-gradient-to-br from-gray-800 to-gray-900 border-2 border-cyan-500/20 hover:border-cyan-500/60 transition-all duration-300 hover:shadow-2xl hover:shadow-cyan-500/20 cursor-pointer"
      style={{
        animation: `slideUp 0.6s ease-out forwards`,
        animationDelay: `${index * 0.1}s`,
        opacity: 0
      }}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      {/* Animated Background Gradient */}
      <div
        className="absolute inset-0 bg-gradient-to-br from-cyan-500/5 via-purple-500/5 to-pink-500/5 opacity-0 group-hover:opacity-100 transition-opacity duration-300"
      />

      {/* Content */}
      <div className="relative z-10">
        {/* Icon Container with Scale Animation */}
        <div
          className="mb-6 flex justify-center"
          style={{
            transform: isHovered ? 'scale(1.15) translateY(-8px)' : 'scale(1)',
            transition: 'transform 0.3s cubic-bezier(0.34, 1.56, 0.64, 1)'
          }}
        >
          {!imageErrors[`skill-${index}`] ? (
            <img
              src={skill.iconSrc}
              alt={skill.name}
              className="w-16 h-16 object-contain drop-shadow-lg"
              onError={() => handleImageError(`skill-${index}`)}
            />
          ) : (
            <div className="w-16 h-16 flex items-center justify-center text-4xl">
              {skill.fallback}
            </div>
          )}
        </div>

        {/* Skill Name */}
        <h3
          className="text-xl font-bold text-center mb-3 text-white group-hover:text-cyan-400 transition-colors duration-300"
        >
          {skill.name}
        </h3>

        {/* Proficiency Percentage with Gradient */}
        <div className="text-center mb-4">
          <span
            className="text-2xl font-bold bg-gradient-to-r from-cyan-400 via-purple-400 to-pink-400 bg-clip-text text-transparent"
          >
            {skill.level}%
          </span>
        </div>

        {/* Enhanced Progress Bar */}
        <div className="h-2 bg-gray-700 rounded-full overflow-hidden mb-3">
          <div
            className={`h-full bg-gradient-to-r ${skill.color} rounded-full shadow-lg transition-all duration-1000 ease-out`}
            style={{
              width: isHovered ? `${skill.level}%` : '0%',
              boxShadow: isHovered ? `0 0 20px rgba(6, 182, 212, 0.6)` : 'none'
            }}
          />
        </div>

        {/* Proficiency Label */}
        <p
          className={`text-xs text-center font-semibold transition-all duration-300 ${
            skill.level >= 90 ? 'text-green-400' :
            skill.level >= 75 ? 'text-cyan-400' :
            'text-purple-400'
          }`}
        >
          {skill.level >= 90 ? 'ðŸ”¥ Expert' : skill.level >= 75 ? 'â­ Proficient' : 'ðŸ“š Learning'}
        </p>
      </div>

      {/* Border Glow on Hover */}
      <div
        className="absolute inset-0 rounded-xl pointer-events-none transition-all duration-300"
        style={{
          borderRadius: '0.75rem',
          background: isHovered
            ? `linear-gradient(135deg, rgba(6, 182, 212, 0.2), rgba(168, 85, 247, 0.2), rgba(236, 72, 153, 0.2))`
            : 'none'
        }}
      />
    </div>
  );
};

// Enhanced CTA Button with Magnetic Effect
const MagneticButton = ({ children, onClick, className, icon: Icon, disabled = false }) => {
  const magneticRef = useMagneticEffect();

  return (
    <button
      ref={magneticRef}
      onClick={onClick}
      disabled={disabled}
      className={`relative overflow-hidden transition-all duration-300 ${className}`}
      style={{
        transitionTimingFunction: 'cubic-bezier(0.34, 1.56, 0.64, 1)'
      }}
    >
      <div className="absolute inset-0 bg-gradient-to-r from-white/20 to-transparent opacity-0 hover:opacity-100 transition-opacity duration-300" />

      <div className="relative flex items-center gap-2">
        {Icon && <Icon size={20} className="group-hover:animate-bounce" />}
        {children}
      </div>
    </button>
  );
};

