  const handleMouseMove = useCallback(
    throttle((e) => {
      if (!isMobile) {
        setMousePosition({
          x: (e.clientX / window.innerWidth - 0.5) * MOUSE_PARALLAX_INTENSITY,
          y: (e.clientY / window.innerHeight - 0.5) * MOUSE_PARALLAX_INTENSITY
        });
      }
